        return ""
    result = []
    q = [root]
    next_level_start = root

    while q:
        curr = q.pop(0)
        if curr == next_level_start:
            if result: # Add newline for previous level, if any
                result.append("#")
            next_level_start = curr.left if curr.left else None

        result.append(str(curr.val))
        if curr.next:
            result.append("->")
            result.append(str(curr.next.val))
        else:
            result.append("-> #N")

        if curr.left:
            q.append(curr.left)
        if curr.right:
